export default function formatMoney(amount = 0) {
  const options = {
    style: 'currency',
    currency: 'USD',
    minimumFractionDigits: 2,
  }

  //check to see if no cents
  if (amount % 100 === 0)
    options.m

  const formatter = Intl.NumberFormat('en-US', options);
  return formatter.format(amount / 100);
}
