import { useQuery } from "@apollo/client"
import gql from 'graphql-tag';

const SINGLE_ITEM_QUERY = gql`
  query {
  Product(where: {
    id: "6068e89a2ea24812864d397e"
  }) {
    name
    price 
    description
  }
}
`;

export default function SingleProductPage({ query }) {

  return <SingleProduct id={query.id} />;
}
